#!/bin/bash
# calculate eval metrics and generate final typing result given typing vocab rankings generated by eval.py

dev="output/yourModelName/epochsXXX/Evaluation_yourDevFile.json"
test="output/yourModelName/epochsXXX/Evaluation_yourTestFile.json"
model_dir="output/yourModelName/epochsXXX"


CUDA_VISIBLE_DEVICES=$device python3 result.py \
                             --dev $dev \
                             --test $test \
                             --model_dir $model_dir \
                             --threshold_step 0.05